example.code:
SELECT DISTINCT ?protein ?title
FROM pubchem:protein
FROM pubchem:measuregroup
FROM pubchem:endpoint
FROM pubchem:substance
WHERE
{
  ?sub rdf:type obo:CHEBI_53289;
     obo:BFO_0000056 ?mg.
  ?mg obo:BFO_0000057 ?protein;
    obo:OBI_0000299 ?ep.
  ?protein rdf:type bp:Protein;
    dcterms:title ?title.
  ?ep rdf:type bao:BAO_0000190;
    obo:IAO_0000136 ?sub;
    sio:has-value ?value.

  filter (?value < 10)
}

SELECT DISTINCT ?title
FROM pubchem:substance
FROM pubchem:measuregroup
FROM pubchem:endpoint
FROM pubchem:protein
FROM pubchem:ontology
WHERE
{
  ?chebi rdfs:subClassOf [
    a owl:Restriction;
    owl:onProperty obo:RO_0000087;
    owl:someValuesFrom obo:CHEBI_37733 ].
  ?sub rdf:type ?chebi;
    obo:BFO_0000056 ?mg.
  ?mg obo:BFO_0000057 ?protein;
    obo:OBI_0000299 ?ep.
  ?protein rdf:type bp:Protein;
    dcterms:title ?title.
  ?ep rdf:type bao:BAO_0000190;
    obo:IAO_0000136 ?sub;
    sio:has-value ?value.

  filter (?value < 10)
}

SELECT DISTINCT ?sub ?protein ?title
FROM pubchem:protein
FROM pubchem:measuregroup
FROM pubchem:endpoint
FROM pubchem:substance
WHERE
{
  ?sub sio:CHEMINF_000477 compound:CID3152;
    obo:BFO_0000056 ?mg.
  ?mg obo:BFO_0000057 ?protein;
    obo:OBI_0000299 ?ep.
  ?protein rdf:type bp:Protein;
    dcterms:title ?title.
  ?ep rdf:type bao:BAO_0000190;
    obo:IAO_0000136 ?sub;
    sio:has-value ?value.

  filter (?value < 10)
}

SELECT DISTINCT ?rolelabel
FROM pubchem:measuregroup
FROM pubchem:endpoint
FROM pubchem:substance
FROM pubchem:ontology
WHERE
{
  ?sub obo:BFO_0000056 ?mg.
  ?mg obo:BFO_0000057 protein:GI7531135;
    obo:OBI_0000299 ?ep.
  ?sub rdf:type ?chebi.
  ?chebi rdfs:subClassOf [
    a owl:Restriction;
    owl:onProperty obo:RO_0000087;
    owl:someValuesFrom ?role ].
  ?role rdfs:label ?rolelabel.
  ?ep obo:IAO_0000136 ?sub;
    rdf:type bao:BAO_0000190;
    sio:has-value ?value.

  filter (?value < 10)
}
